function jsonToObject<T>(jsonStr: string, objType: { new (): T }): T {
    try {
        const obj = new objType();
        const parsedJson = JSON.parse(jsonStr);

        // Iterate over the properties of the interface and set them on the object
        Object.keys(obj).forEach(key => {
            if (parsedJson.hasOwnProperty(key)) {
                const value = parsedJson[key];
                const propType = objType.prototype[key];
                if (typeof propType === 'string') {
                    obj[key] = String(value);
                } else if (typeof propType === 'number') {
                    obj[key] = Number(value);
                } else if (typeof propType === 'boolean') {
                    obj[key] = Boolean(value);
                } else if (Array.isArray(propType)) {
                    obj[key] = Array.isArray(value) ? value : [];
                } else {
                    obj[key] = value;
                }
            }
        });

        return obj;
    } catch (error) {
        console.error("Error parsing JSON:", error);
        return objType.new(); // Return an empty instance of the type
    }
}

// Example usage
const jsonStr = '{"name": "John Doe", "age": 30, "email": "john@example.com"}';
const user: User = jsonToObject<User>(jsonStr, User);
console.log(user); // Output: { name: 'John Doe', age: 30, email: 'john@example.com' }

