double median(int* arr, int size) {
    // Find the middle index
    int middle = size / 2;
    // Sort the array in ascending order
    int temp;
    for (int i = 0; i < size; i++) {
        if (i == middle) {
            temp = arr[i];
        } else if (arr[i] < arr[middle]) {
            temp = arr[i];
        } else {
