```java
import org.lwjgl.glfw.GLFW;
import org.lwjgl.opengl.GL;
import org.lwjgl.opengl.GL33;
import org.joml.*;

public class ConfusingSquareRenderer {
    private long window;
    private int vertexShaderID, fragmentShaderID, shaderProgramID;
    private int vertexBufferObject, vertexArrayObject;

    public void init() {
        // Initialize GLFW
        if (!GLFW.glfwInit()) {
            throw new IllegalStateException("Unable to initialize GLFW");
        }

        // Create a windowed mode window and its OpenGL context
        window = GLFW.glfwCreateWindow(800, 600, "Confusing Square", NULL, NULL);
        if (window == NULL) {
            throw new RuntimeException("Failed to create the GLFW window");
        }

        // Make the window's context current
        GLFW.glfwMakeContextCurrent(window);

        // Enable v-sync
        GLFW.glfwSwapInterval(1);

        // Set the required OpenGL version
        GL.createCapabilities();
        GL33.glViewport(0, 0, 800, 600);

        // Initialize vertex and fragment shaders
        vertexShaderID = GL33.glCreateShader(GL33.GL_VERTEX_SHADER);
        fragmentShaderID = GL33.glCreateShader(GL33.GL_FRAGMENT_SHADER);

        // Compile vertex shader
        GL33.glShaderSource(vertexShaderID, "attribute vec2 position;\nvoid main() {\n  gl_Position = vec4(position, 0.0, 1.0);\n}");
        GL33.glCompileShader(vertexShaderID);

        // Compile fragment shader
        GL33.glShaderSource(fragmentShaderID, "precision mediump float;\nuniform vec4 color;\nvoid main() {\n  gl_FragColor = color;\n}");
        GL33.glCompileShader(fragmentShaderID);

        // Link the program
        shaderProgramID = GL33.glCreateProgram();
        GL33.glAttachShader(shaderProgramID, vertexShaderID);
        GL33.glAttachShader(shaderProgramID, fragmentShaderID);
        GL33.glLinkProgram(shaderProgramID);

        // Create VAO, VBO for vertices
        vertexBufferObject = GL33.glGenBuffers();
        vertexArrayObject = GL33.glGenVertexArrays();

        // Bind VAO
        GL33.glBindVertexArray(vertexArrayObject);

        // Bind VBO
        GL33.glBindBuffer(GL33.GL_ARRAY_BUFFER, vertexBufferObject);

        // Define vertices
        float[] vertices = {-0.5f, -0.5f, 0.5f, -0.5f, 0.5f, 0.5f, -0.5f, 0.5f};
        GL33.glBufferData(GL33.GL_ARRAY_BUFFER, BufferUtils.createFloatBuffer(vertices), GL33.GL_STATIC_DRAW);

        // Enable vertex attribute array
        GL33.glEnableVertexAttribArray(0);
        GL33.glVertexAttribPointer(0, 2, GL33.GL_FLOAT, false, 0, 0);

        // Unbind VAO
        GL33.glBindVertexArray(0);

        // Main loop
        while (!GLFW.glfwWindowShouldClose(window)) {
            // Render here
            GL33.glClear(GL33.GL_COLOR_BUFFER_BIT);

            // Use shader program
            GL33.glUseProgram(shaderProgramID);

            // Set color uniform
            float[] color = {1.0f, 0.0f, 0.0f, 1.0f}; // Red color
            GL33.glUniform4fv(GL33.glGetUniformLocation(shaderProgramID, "color"), 1, color, 0);

            // Draw the square
            GL33.glBindVertexArray(vertexArrayObject);
            GL33.glDrawArrays(GL33.GL_TRIANGLE_STRIP, 0, 4);

            // Swap buffers
            GLFW.glfwSwapBuffers(window);

            // Poll for window events
            GLFW.glfwPollEvents();
        }

        // Clean up
        GL33.glDeleteShader(vertexShaderID);
        GL33.glDeleteShader(fragmentShaderID);
        GL33.glDeleteProgram(shaderProgramID);
        GL33.glDeleteBuffers(vertexBufferObject);
        GL33.glDeleteVertexArrays(vertexArrayObject);

        // Terminate GLFW
        GLFW.glfwTerminate();
    }

    public static void main(String[] args) {
        new ConfusingSquareRenderer().init();
    }
}
