// Define the pins
const int trigPin = 9;   // Trig pin connected to digital pin 9
const int echoPin = 10;  // Echo pin connected to digital pin 10
const int ledPin = 13;   // LED connected to digital pin 13

// Variables to store the time and distance
long duration;
int distance;

void setup() {
  // Initialize the serial communication
  Serial.begin(9600);

  // Set the trig pin as output and echo pin as input
  pinMode(trigPin, OUTPUT);
  pinMode(echoPin, INPUT);
  pinMode(ledPin, OUTPUT);
}

void loop() {
  // Send pulse to trigger pin
  digitalWrite(trigPin, LOW);
  delayMicroseconds(2);
  digitalWrite(trigPin, HIGH);
  delayMicroseconds(10);
  digitalWrite(trigPin, LOW);

  // Read the echo pin
  duration = pulseIn(echoPin, HIGH);

  // Convert the duration to distance in cm
  distance = duration * 0.034 / 2;

  // Print the distance to the Serial Monitor
  Serial.print("Distance: ");
  Serial.println(distance);

  // Blink the LED based on the distance
  if (distance < 50) {  // Example threshold
    blinkFaster();
  } else if (distance >= 50 && distance < 100) {
    blinkNormal();
  } else {
    blinkSlower();
  }
}

void blinkFaster() {
  // Blink LED at a faster rate
  int interval = 100;  // Adjust as needed
  blinkLED(interval);
}

void blinkNormal() {
  // Blink LED at a normal rate
  int interval = 200;  // Adjust as needed
  blinkLED(interval);
}

void blinkSlower() {
  // Blink LED at a slower rate
  int interval = 500;  // Adjust as needed
  blinkLED(interval);
}

void blinkLED(int interval) {
  while (true) {
    digitalWrite(ledPin, HIGH);  // Turn LED on
    delay(interval);
    digitalWrite(ledPin, LOW);   // Turn LED off
    delay(interval);
  }
}
```

### Explanation:
- **trigPin**: The trigger pin of the ultrasonic sensor.
- **echoPin**: The echo pin of the ultrasonic sensor.
- **ledPin**: The pin connected to the LED.
- **blinkFaster(), blinkNormal(), blinkSlower()**: Functions to control the blinking speed of the LED.
- **blinkLED(int interval)**: A function to handle the blinking of the LED at a specified interval.

### Notes:
- Adjust the intervals (`interval`) in the `blinkLED` function to change how fast or slow the LED blinks.
- The example thresholds (50 and 100 cm) are just placeholders. You can adjust them based on your specific requirements.

